import { Component } from "@angular/core";

@Component({
  selector: "app-tecnologies",
  templateUrl: "./tecnologies.component.html",
  styleUrls: ["./tecnologies.component.scss"],
})
export class TecnologiesComponent {
  tecnologies = [
    {
      title: "Lenguajes",
      items: [
        { title: "JavaScript" },
        { title: "TypeScript" },
        { title: "Python" },
        { title: "C++" },
      ],
    },
    {
      title: "Frontend",
      items: [
        { title: "JavaScript" },
        { title: "TypeScript" },
        { title: "Python" },
        { title: "C++" },
      ],
    },
    {
      title: "Backend",
      items: [
        { title: "JavaScript" },
        { title: "TypeScript" },
        { title: "Python" },
        { title: "C++" },
      ],
    },
    {
      title: "DevOps",
      items: [
        { title: "JavaScript" },
        { title: "TypeScript" },
        { title: "Python" },
        { title: "C++" },
      ],
    },
  ];
}
<div class="container">
  <div *ngFor="let item of tecnologies" [ngClass]="item.title">
    <h1>{{ item.title }}</h1>
    <div *ngFor="let subItem of item.items"></div>
  </div>
</div>
