import { Component } from "@angular/core";

@Component({
  selector: "app-navbar",
  templateUrl: "./navbar.component.html",
  styleUrls: ["./navbar.component.scss"],
})
export class NavbarComponent {
  hamburguer: boolean = false;
  items = [
    {
      title: "Home",
      link: "",
      icon: "",
    },
    {
      title: "Proyectos",
      link: "projects",
      icon: "",
    },
    {
      title: "Sobre mi",
      link: "about",
      icon: "",
    },
    {
      title: "Tecnologias",
      link: "tecnologies",
      icon: "",
    },
  ];

  handleMenu() {
    this.hamburguer = !this.hamburguer;
  }
}
<nav id="nav">
  <div class="container">
    <div class="container-resposive">
      <img src="/assets/main.png" alt="not found" routerLink="/" />
      <div
        class="container-hamb"
        (click)="handleMenu()"
        [ngClass]="hamburguer ? 'open' : ''"
      >
        <div class="hamburguer" id="one"></div>
        <div class="hamburguer" id="two"></div>
        <div class="hamburguer" id="three"></div>
      </div>
    </div>
    <div class="item-container">
      <div
        id="item"
        class="item"
        *ngFor="let item of items"
        [routerLink]="item.link"
      >
        {{ item.title }}
        <label class="item-l" for="item"></label>
      </div>
    </div>
  </div>
</nav>
