import { Component } from "@angular/core";
import { Router, NavigationEnd } from "@angular/router";

@Component({
  selector: "app-navbar",
  templateUrl: "./navbar.component.html",
  styleUrls: ["./navbar.component.scss"],
})
export class NavbarComponent {
  hamburguer: boolean = false;
  routeActually = "";
  items = [
    {
      title: "Home",
      link: "",
      icon: "",
    },
    {
      title: "Proyectos",
      link: "projects",
      icon: "",
    },
    {
      title: "Sobre mi",
      link: "about",
      icon: "",
    },
    {
      title: "Tecnologias",
      link: "tecnologies",
      icon: "",
    },
  ];

  constructor(private router: Router) {
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.routeActually = this.router.url;
      }
    });
  }

  ngOninit() {}

  handleMenu() {
    this.hamburguer = !this.hamburguer;
  }
}
<nav id="nav">
  <div class="container" [ngClass]="hamburguer ? 'open' : ''">
    <div class="container-resposive">
      <img src="/assets/main.png" alt="not found" routerLink="/" />
      <div
        class="container-hamb"
        (click)="handleMenu()"
        [ngClass]="hamburguer ? 'open' : ''"
      >
        <div class="hamburguer" id="one"></div>
        <div class="hamburguer" id="two"></div>
        <div class="hamburguer" id="three"></div>
      </div>
    </div>
    <div class="item-container" [ngClass]="hamburguer ? 'open' : ''">
      <div
        id="item"
        class="item"
        *ngFor="let item of items"
        [routerLink]="item.link"
        (click)="handleMenu()"
        [ngClass]="'/' + item.link == routeActually ? 'active' : 'none'"
      >
        {{ item.title }}
        <label class="item-l" for="item"></label>
      </div>
    </div>
  </div>
</nav>
