import { Component } from "@angular/core";

@Component({
  selector: "app-about",
  templateUrl: "./about.component.html",
  styleUrls: ["./about.component.scss"],
})
export class AboutComponent {
  estudios = [
    {
      title: "Tecnicatura Universitaria en Inteligencia Artificial",
      subTitle: {
        title: "Universidad Nacional de Rosario",
        link: "https://unr.edu.ar/",
      },
      fecha: "Febrero de 2023 - Diciembre 2025",
      descripcion: "",
    },
    {
      title: "SkillUp Node",
      subTitle: { title: "Alkemy", link: "https://www.alkemy.org/" },
      fecha: "1 de noviembre de 2022 - 16 noviembre de 2022",
      descripcion: "",
    },
    {
      title: "Bootcamp FullStack PERN",
      subTitle: { title: "Henry", link: "https://www.soyhenry.com/" },
      fecha: "Diciembre 2021 - Agosto 2022",
      descripcion: "",
    },
  ];
}
<div id="about">
  <div class="container">
    <h1>Estudios</h1>

    <div class="container-card">
      <div class="card" *ngFor="let card of estudios">
        <div class="first">
          <h1>{{ card.title }}</h1>
          <a [href]="card.subTitle.link" target="_blank">{{
            card.subTitle.title
          }}</a>
          <p>{{ card.fecha }}</p>
        </div>
        <p>{{ card.descripcion }}</p>
      </div>
    </div>
  </div>
</div>
