import { Component } from "@angular/core";

@Component({
  selector: "app-navbar",
  templateUrl: "./navbar.component.html",
  styleUrls: ["./navbar.component.scss"],
})
export class NavbarComponent {
  items = [
    {
      title: "Home",
      link: "",
      icon: "",
    },
    {
      title: "Proyectos",
      link: "projects",
      icon: "",
    },
    {
      title: "Sobre mi",
      link: "about",
      icon: "",
    },
    {
      title: "Tecnologias",
      link: "tecnologies",
      icon: "",
    },
  ];
}
<nav id="nav">
  <div class="container">
    <img src="/assets/main.png" alt="not found" routerLink="/" />
    <div class="hamburguer"></div>
    <div class="item-container">
      <div
        id="item"
        class="item"
        *ngFor="let item of items"
        [routerLink]="item.link"
      >
        {{ item.title }}
        <label class="item-l" for="item"></label>
      </div>
    </div>
  </div>
</nav>
