import { Component } from "@angular/core";

@Component({
  selector: "app-projects",
  templateUrl: "./projects.component.html",
  styleUrls: ["./projects.component.scss"],
})
export class ProjectsComponent {
  projects = [
    {
      title: "",
      desciption: "",
      tecnologias: [
        {
          title: "",
        },
      ],
    },
    {
      title: "Wallet digital",
      desciption: "Proyecto grupal",
      tecnologias: [
        {
          title: "",
        },
      ],
    },
    {
      title: "E-commerce",
      desciption: "Proyecto grupal",
      tecnologias: [
        {
          title: "",
        },
      ],
    },
    {
      title: "Pokemon",
      desciption: "Proyecto full-stack",
      tecnologias: [
        {
          title: "",
        },
      ],
    },
  ];
}
<div id="projects">
  <section id="laboral">
    <h1>Laboral</h1>
    <div class="container"></div>
  </section>

  <section id="proyectos">
    <h1>Proyectos</h1>
    <div class="container">
      <div class="card" *ngFor="let card of projects">
        <h1>{{ card.title }}</h1>
        <p>{{ card.desciption }}</p>
        <div *ngFor="let item of card.tecnologias">
          <p>{{ item.title }}</p>
        </div>
      </div>
    </div>
  </section>
</div>
