import { Component } from "@angular/core";
import { faLinkedin, faInstagram } from "@fortawesome/free-brands-svg-icons";
import { faChessKnight, faCode } from "@fortawesome/free-solid-svg-icons";

@Component({
  selector: "app-footer",
  templateUrl: "./footer.component.html",
  styleUrls: ["./footer.component.scss"],
})
export class FooterComponent {
  columns = [
    {
      title: "Sobre mi",
      subColumns: [
        {
          title: "",
          link: "",
          icon: faLinkedin,
        },
      ],
    },
    {
      title: "Redes sociales",
      subColumns: [
        {
          title: "Linkedin",
          link: "https://www.linkedin.com/in/bruno-developer/",
          icon: faLinkedin,
        },
        {
          title: "Instagram",
          link: "https://www.instagram.com/brunod.01/",
          icon: faInstagram,
        },
        {
          title: "CODEFORCES",
          link: "https://codeforces.com/profile/DevidB",
          icon: faCode,
        },
        {
          title: "LICHESS",
          link: "https://lichess.org/@/DeividL",
          icon: faChessKnight,
        },
      ],
    },
    {
      title: "Contacto",
      subColumns: [
        {
          title: "WhatsApp",
          link: "",
          icon: faLinkedin,
        },
        {
          title: "Telegram",
          link: "",
          icon: faLinkedin,
        },
        {
          title: "Mail",
          link: "",
          icon: faLinkedin,
        },
      ],
    },
  ];
}
<footer id="footer">
  <div class="container">
    <div class="column" *ngFor="let item of columns">
      <h1>{{ item.title }}</h1>
      <a
        class="subItem"
        *ngFor="let subItem of item.subColumns"
        [href]="subItem.link"
        target="_blank"
      >
        <fa-icon [icon]="subItem.icon"></fa-icon>
        <p>
          {{ subItem.title }}
        </p>
      </a>
    </div>
  </div>
</footer>
