import { Component } from "@angular/core";

@Component({
  selector: "app-footer",
  templateUrl: "./footer.component.html",
  styleUrls: ["./footer.component.scss"],
})
export class FooterComponent {
  columns = [
    {
      title: "prueba",
      subColumns: [
        {
          title: "",
        },
      ],
    },
    {
      title: "prueba",
      subColumns: [
        {
          title: "",
        },
      ],
    },
    {
      title: "prueba",
      subColumns: [
        {
          title: "",
        },
      ],
    },
  ];
}
<footer id="footer">
  <div class="container">
    <div class="column" *ngFor="let item of columns">
      <div class="item">
        <h1>{{ item.title }}</h1>
      </div>
    </div>
  </div>
</footer>
