import { Component } from "@angular/core";
import {
  faLinkedin,
  faInstagram,
  faWhatsapp,
  faTelegram,
  faDiscord,
} from "@fortawesome/free-brands-svg-icons";
import {
  faChessKnight,
  faCode,
  faEnvelope,
  faSignature,
  faFile,
} from "@fortawesome/free-solid-svg-icons";

@Component({
  selector: "app-footer",
  templateUrl: "./footer.component.html",
  styleUrls: ["./footer.component.scss"],
})
export class FooterComponent {
  columns = [
    {
      title: "Sobre mi",
      subColumns: [
        {
          title: "Bruno David",
          link: "",
          icon: faSignature,
        },
        {
          title: "Mi cv",
          link: "",
          icon: faFile,
        },
      ],
    },
    {
      title: "Redes sociales",
      subColumns: [
        {
          title: "Linkedin",
          link: "https://www.linkedin.com/in/bruno-developer/",
          icon: faLinkedin,
        },
        {
          title: "Discord",
          link: "https://www.linkedin.com/in/bruno-developer/",
          icon: faDiscord,
        },
        {
          title: "Instagram",
          link: "https://www.instagram.com/brunod.01/",
          icon: faInstagram,
        },
        {
          title: "CODEFORCES",
          link: "https://codeforces.com/profile/DevidB",
          icon: faCode,
        },
        {
          title: "LICHESS",
          link: "https://lichess.org/@/DeividL",
          icon: faChessKnight,
        },
      ],
    },
    {
      title: "Contacto",
      subColumns: [
        {
          title: "WhatsApp",
          link: "https://wa.me/543412705762",
          icon: faWhatsapp,
        },
        {
          title: "Telegram",
          link: "https://t.me/brunodavidl",
          icon: faTelegram,
        },
        {
          title: "Mail",
          link: "mailto:bruno.david9914@gmail.com?",
          icon: faEnvelope,
        },
      ],
    },
  ];
}
<footer id="footer">
  <div class="container">
    <div class="column" *ngFor="let item of columns">
      <h1>{{ item.title }}</h1>
      <a
        class="subItem"
        *ngFor="let subItem of item.subColumns"
        [href]="subItem.link"
        target="_blank"
      >
        <fa-icon [icon]="subItem.icon"></fa-icon>
        <p>
          {{ subItem.title }}
        </p>
      </a>
    </div>
  </div>
  <p>v1.0.0</p>
</footer>
