import { Component } from "@angular/core";

@Component({
  selector: "app-tecnologies",
  templateUrl: "./tecnologies.component.html",
  styleUrls: ["./tecnologies.component.scss"],
})
export class TecnologiesComponent {
  tecnologies = [
    {
      title: "Lenguajes",
      items: [
        { title: "JavaScript" },
        { title: "TypeScript" },
        { title: "Python" },
        { title: "C++" },
      ],
    },
    {
      title: "Frontend",
      items: [
        { title: "Angular" },
        { title: "React" },
        { title: "React Native" },
        { title: "Redux" },
        { title: "CSS" },
        { title: "HTML" },
        { title: "Boostrap" },
      ],
    },
    {
      title: "Backend",
      items: [
        { title: "NodeJs" },
        { title: "Express" },
        { title: "SQL" },
        { title: "MongoDb" },
        { title: "Postman" },
      ],
    },
    {
      title: "DevOps",
      items: [{ title: "Docker" }, { title: "Ubuntu" }, { title: "Bash" }],
    },
  ];
}
<div class="container">
  <div *ngFor="let item of tecnologies" [ngClass]="item.title">
    <h1>{{ item.title }}</h1>
    <div *ngFor="let subItem of item.items">
      <p>{{ subItem.title }}</p>
    </div>
  </div>
</div>
